snippets.script:
&lt;script&gt;
    import {
        Dot,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="dot-palette"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Dot /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACCENT&lt;/Text&gt;
        &lt;Dot palette="accent" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DARK&lt;/Text&gt;
        &lt;Dot palette="dark" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LIGHT&lt;/Text&gt;
        &lt;Dot palette="light" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ALERT&lt;/Text&gt;
        &lt;Dot palette="alert" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;AFFIRMATIVE&lt;/Text&gt;
        &lt;Dot palette="affirmative" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;NEGATIVE&lt;/Text&gt;
        &lt;Dot palette="negative" /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.dot-palette .dot) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Dot,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="dot-animation"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Dot /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;PULSE&lt;/Text&gt;
        &lt;Dot animation="pulse" /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.dot-animation .dot) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Button,
        Dot,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;Text&gt;
        Sample Text (RAISED)
        &lt;Dot palette="accent" position="raised" /&gt;
    &lt;/Text&gt;

    &lt;div&gt;
        &lt;Button palette="accent"&gt;
            Open Inbox (FLOATED)
            &lt;Dot
                palette="negative"
                position="floated"
            /&gt;
        &lt;/Button&gt;
    &lt;/div&gt;
&lt;/Stack&gt;
