snippets.script:
&lt;script&gt;
    import {
        Button,
        Ellipsis,
        Heading,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack alignment_x="left" spacing="medium"&gt;
    &lt;Button disabled&gt;
        &lt;!--
            NOTE: Wrapping text and Component together to
            prevent `Ellipsis` from getting icon spacing
        --&gt;
        &lt;Text is="span"&gt;
            Submit
            &lt;Ellipsis /&gt;
        &lt;/Text&gt;
    &lt;/Button&gt;

    &lt;Heading&gt;
        Loading
        &lt;Ellipsis /&gt;
    &lt;/Heading&gt;
&lt;/Stack&gt;

&lt;script&gt;
    import {
        Ellipsis,
        Heading,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Heading&gt;
    Loading
    &lt;Ellipsis character="?" /&gt;
&lt;/Heading&gt;
