snippets.script:
&lt;script&gt;
    import {Hero, Wave} from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Hero.Container margin_top="medium"&gt;
    &lt;Hero.Header&gt;Loading&lt;/Hero.Header&gt;
    &lt;Hero.Section padding_bottom="medium"&gt;
        &lt;Wave size="tiny" /&gt;
    &lt;/Hero.Section&gt;
&lt;/Hero.Container&gt;

&lt;script&gt;
    import {
        Stack,
        Text,
        Wave,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="wave-palette"
    spacing="medium"
    orientation="horizontal"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Wave /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACCENT&lt;/Text&gt;
        &lt;Wave palette="accent" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DARK&lt;/Text&gt;
        &lt;Wave palette="dark" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LIGHT&lt;/Text&gt;
        &lt;Wave palette="light" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ALERT&lt;/Text&gt;
        &lt;Wave palette="alert" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;AFFIRMATIVE&lt;/Text&gt;
        &lt;Wave palette="affirmative" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;NEGATIVE&lt;/Text&gt;
        &lt;Wave palette="negative" /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.wave-palette .wave) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Stack,
        Text,
        Wave,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="wave-size"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Wave /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;TINY&lt;/Text&gt;
        &lt;Wave size="tiny" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;SMALL&lt;/Text&gt;
        &lt;Wave size="small" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;MEDIUM&lt;/Text&gt;
        &lt;Wave size="medium" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LARGE&lt;/Text&gt;
        &lt;Wave size="large" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;HUGE&lt;/Text&gt;
        &lt;Wave size="huge" /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.wave-size .wave) {
        display: block;
    }
&lt;/style&gt;
