&lt;script&gt;
    import {
        Check,
        Form,
        Stack,
        Text,
    } from "@kahi-ui/framework";

    let logic_state = ["cheese", "onion"];
&lt;/script&gt;

&lt;Text is="strong"&gt;
    Select Pizza Toppings
    &lt;Text is="sup"&gt;{logic_state}&lt;/Text&gt;
&lt;/Text&gt;

&lt;Stack
    orientation="horizontal"
    spacing="small"
    variation="wrap"
    margin_top="small"
&gt;
    &lt;Form.Group name="check-preview" bind:logic_state&gt;
        &lt;Check
            id="check-preview-cheese"
            palette="accent"
            size="tiny"
            value="cheese"
        &gt;
            Cheese
        &lt;/Check&gt;

        &lt;Check
            id="check-preview-pepperoni"
            palette="accent"
            size="tiny"
            value="pepperoni"
        &gt;
            Pepperoni
        &lt;/Check&gt;

        &lt;Check
            id="check-preview-onion"
            palette="accent"
            size="tiny"
            value="onion"
        &gt;
            Onion
        &lt;/Check&gt;

        &lt;Check
            id="check-preview-pineapple"
            palette="accent"
            size="tiny"
            value="pineapple"
        &gt;
            Pineapple
        &lt;/Check&gt;
    &lt;/Form.Group&gt;
&lt;/Stack&gt;

&lt;script&gt;
    import {
        Check,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="check-states"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Check /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;STATE&lt;/Text&gt;
        &lt;Check state /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACTIVE NO-STATE&lt;/Text&gt;
        &lt;Check active /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACTIVE STATE&lt;/Text&gt;
        &lt;Check active state /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DISABLED NO-STATE&lt;/Text&gt;
        &lt;Check disabled /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DISABLED STATE&lt;/Text&gt;
        &lt;Check disabled state /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.check-states strong) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Check,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="check-palette"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Check /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACCENT&lt;/Text&gt;
        &lt;Check palette="accent" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DARK&lt;/Text&gt;
        &lt;Check palette="dark" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LIGHT&lt;/Text&gt;
        &lt;Check palette="light" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ALERT&lt;/Text&gt;
        &lt;Check palette="alert" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;AFFIRMATIVE&lt;/Text&gt;
        &lt;Check palette="affirmative" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;NEGATIVE&lt;/Text&gt;
        &lt;Check palette="negative" /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.check-palette strong) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Check,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="check-size"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Check /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;TINY&lt;/Text&gt;
        &lt;Check size="tiny" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;SMALL&lt;/Text&gt;
        &lt;Check size="small" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;MEDIUM&lt;/Text&gt;
        &lt;Check size="medium" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LARGE&lt;/Text&gt;
        &lt;Check size="large" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;HUGE&lt;/Text&gt;
        &lt;Check size="huge" /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.check-size strong) {
        display: block;
    }
&lt;/style&gt;
