snippets.script:
&lt;script&gt;
    import {
        Form,
        Radio,
        Stack,
        Text,
    } from "@kahi-ui/framework";

    let logic_state = "vanilla";
&lt;/script&gt;

&lt;Text is="strong"&gt;
    Select an Ice Cream Flavor
    &lt;Text is="sup"&gt;{logic_state}&lt;/Text&gt;
&lt;/Text&gt;

&lt;Stack spacing="small" margin_top="small"&gt;
    &lt;Form.Group
        logic_name="radio-preview"
        bind:logic_state
    &gt;
        &lt;Radio
            id="radio-preview-bubblegum"
            palette="accent"
            size="tiny"
            value="bubblegum"
        &gt;
            Bubblegum
        &lt;/Radio&gt;

        &lt;Radio
            id="radio-preview-chocolate"
            palette="accent"
            size="tiny"
            value="chocolate"
        &gt;
            Chocolate
        &lt;/Radio&gt;

        &lt;Radio
            id="radio-preview-vanilla"
            palette="accent"
            size="tiny"
            value="vanilla"
        &gt;
            Vanilla
        &lt;/Radio&gt;
    &lt;/Form.Group&gt;
&lt;/Stack&gt;

&lt;script&gt;
    import {
        Radio,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="radio-states"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Radio /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;STATE&lt;/Text&gt;
        &lt;Radio state /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACTIVE NO-STATE&lt;/Text&gt;
        &lt;Radio active /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACTIVE STATE&lt;/Text&gt;
        &lt;Radio active state /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DISABLED NO-STATE&lt;/Text&gt;
        &lt;Radio disabled /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DISABLED STATE&lt;/Text&gt;
        &lt;Radio disabled state /&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.radio-states strong) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Form,
        Radio,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Form.Group logic_name="radio-palette"&gt;
    &lt;Stack
        class="radio-palette"
        orientation="horizontal"
        spacing="medium"
        variation="wrap"
    &gt;
        &lt;div&gt;
            &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
            &lt;Radio /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;ACCENT&lt;/Text&gt;
            &lt;Radio palette="accent" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;DARK&lt;/Text&gt;
            &lt;Radio palette="dark" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;LIGHT&lt;/Text&gt;
            &lt;Radio palette="light" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;ALERT&lt;/Text&gt;
            &lt;Radio palette="alert" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;AFFIRMATIVE&lt;/Text&gt;
            &lt;Radio palette="affirmative" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;NEGATIVE&lt;/Text&gt;
            &lt;Radio palette="negative" /&gt;
        &lt;/div&gt;
    &lt;/Stack&gt;
&lt;/Form.Group&gt;

&lt;style&gt;
    :global(.radio-palette strong) {
        display: block;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Form,
        Radio,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Form.Group logic_name="radio-size"&gt;
    &lt;Stack
        class="radio-size"
        orientation="horizontal"
        spacing="medium"
        variation="wrap"
    &gt;
        &lt;div&gt;
            &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
            &lt;Radio /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;TINY&lt;/Text&gt;
            &lt;Radio size="tiny" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;SMALL&lt;/Text&gt;
            &lt;Radio size="small" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;MEDIUM&lt;/Text&gt;
            &lt;Radio size="medium" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;LARGE&lt;/Text&gt;
            &lt;Radio size="large" /&gt;
        &lt;/div&gt;

        &lt;div&gt;
            &lt;Text is="strong"&gt;HUGE&lt;/Text&gt;
            &lt;Radio size="huge" /&gt;
        &lt;/div&gt;
    &lt;/Stack&gt;
&lt;/Form.Group&gt;

&lt;style&gt;
    :global(.radio-size strong) {
        display: block;
    }
&lt;/style&gt;
