&lt;script&gt;
    import {
        Divider,
        Mosaic,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Mosaic sizing="medium" spacing="medium"&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Divider /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ACCENT&lt;/Text&gt;
        &lt;Divider palette="accent" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;DARK&lt;/Text&gt;
        &lt;Divider palette="dark" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LIGHT&lt;/Text&gt;
        &lt;Divider palette="light" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;ALERT&lt;/Text&gt;
        &lt;Divider palette="alert" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;AFFIRMATIVE&lt;/Text&gt;
        &lt;Divider palette="affirmative" /&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;NEGATIVE&lt;/Text&gt;
        &lt;Divider palette="negative" /&gt;
    &lt;/div&gt;
&lt;/Mosaic&gt;

&lt;script&gt;
    import {
        Box,
        Divider,
        Mosaic,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="divider-orientation"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Divider /&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;VERTICAL&lt;/Text&gt;
        &lt;Box
            palette="dark"
            padding="small"
            width="content-min"
        &gt;
            &lt;Divider orientation="vertical" /&gt;
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.divider-orientation
            &gt; div:first-child
            &gt; .box) {
        display: flex;
        align-items: center;
        height: 2rem;
        width: 6rem;
    }

    :global(.divider-orientation
            &gt; div:last-child
            &gt; .box) {
        height: 6rem;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Box,
        Divider,
        Mosaic,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="divider-text"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Divider&gt;DEFAULT&lt;/Divider&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Divider orientation="vertical"&gt;
                VERTICAL
            &lt;/Divider&gt;
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.divider-text &gt; div:first-child &gt; .box) {
        width: 12rem;
    }

    :global(.divider-text &gt; div:last-child &gt; .box) {
        display: flex;
        justify-content: center;

        width: 3rem;
        height: 12rem;
    }
&lt;/style&gt;
