&lt;script&gt;
    import {
        Button,
        Box,
        Group,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Group&gt;
                &lt;Button&gt;Button 1&lt;/Button&gt;
                &lt;Button&gt;Button 2&lt;/Button&gt;
                &lt;Button&gt;Button 3&lt;/Button&gt;
            &lt;/Group&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;VERTICAL&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Group orientation="vertical"&gt;
                &lt;Button&gt;Button 1&lt;/Button&gt;
                &lt;Button&gt;Button 2&lt;/Button&gt;
                &lt;Button&gt;Button 3&lt;/Button&gt;
            &lt;/Group&gt;
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;script&gt;
    import {
        Figure,
        Group,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="group-stacked"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Group variation="stacked"&gt;
            &lt;Figure
                variation="icon"
                size="medium"
                shape="pill"
            &gt;
                &lt;img src={IMAGE_AVATAR} /&gt;
            &lt;/Figure&gt;

            &lt;Figure
                variation="icon"
                size="medium"
                shape="pill"
            &gt;
                &lt;img src={IMAGE_AVATAR} /&gt;
            &lt;/Figure&gt;

            &lt;Figure
                variation="icon"
                size="medium"
                shape="pill"
            &gt;
                &lt;img src={IMAGE_AVATAR} /&gt;
            &lt;/Figure&gt;

            &lt;Stack
                alignment_y="center"
                padding_left="small"
            &gt;
                &lt;Text is="small"&gt;+7&lt;/Text&gt;
            &lt;/Stack&gt;
        &lt;/Group&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;VERTICAL&lt;/Text&gt;
        &lt;Group
            variation="stacked"
            orientation="vertical"
        &gt;
            &lt;Figure
                variation="icon"
                size="medium"
                shape="pill"
            &gt;
                &lt;img src={IMAGE_AVATAR} /&gt;
            &lt;/Figure&gt;

            &lt;Figure
                variation="icon"
                size="medium"
                shape="pill"
            &gt;
                &lt;img src={IMAGE_AVATAR} /&gt;
            &lt;/Figure&gt;

            &lt;Figure
                variation="icon"
                size="medium"
                shape="pill"
            &gt;
                &lt;img src={IMAGE_AVATAR} /&gt;
            &lt;/Figure&gt;

            &lt;Stack
                alignment_x="center"
                padding_top="small"
            &gt;
                &lt;Text is="small"&gt;+7&lt;/Text&gt;
            &lt;/Stack&gt;
        &lt;/Group&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.group-stacked strong) {
        display: block;
    }
&lt;/style&gt;
