&lt;script&gt;
    import {
        Box,
        Mosaic,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="mosaic-sizing"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;TINY&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Mosaic sizing="tiny" spacing="medium"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Mosaic&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;SMALL&lt;/Text&gt;

        &lt;Box palette="dark" padding="small"&gt;
            &lt;Mosaic sizing="small" spacing="medium"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Mosaic&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;MEDIUM&lt;/Text&gt;

        &lt;Box palette="dark" padding="small"&gt;
            &lt;Mosaic sizing="medium" spacing="medium"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Mosaic&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LARGE&lt;/Text&gt;

        &lt;Box palette="dark" padding="small"&gt;
            &lt;Mosaic sizing="large" spacing="medium"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Mosaic&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;HUGE&lt;/Text&gt;

        &lt;Box palette="dark" padding="small"&gt;
            &lt;Mosaic sizing="huge" spacing="medium"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Mosaic&gt;
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.mosaic-sizing .mosaic .box) {
        height: 3rem;
    }
&lt;/style&gt;

