&lt;script&gt;
    import {
        Box,
        Spacer,
        Stack,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Box
    palette="auto-inverse"
    padding_x="small"
    padding_y="tiny"
&gt;
    &lt;Stack orientation="horizontal"&gt;
        LEFT
        &lt;Spacer /&gt;
        RIGHT
    &lt;/Stack&gt;
&lt;/Box&gt;

&lt;script&gt;
    import {
        Box,
        Spacer,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;TINY&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            padding_x="small"
            padding_y="tiny"
        &gt;
            TOP
            &lt;Spacer spacing="tiny" /&gt;
            BOTTOM
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;SMALL&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            padding_x="small"
            padding_y="tiny"
        &gt;
            TOP
            &lt;Spacer spacing="small" /&gt;
            BOTTOM
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;MEDIUM&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            padding_x="small"
            padding_y="tiny"
        &gt;
            TOP
            &lt;Spacer spacing="medium" /&gt;
            BOTTOM
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LARGE&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            padding_x="small"
            padding_y="tiny"
        &gt;
            TOP
            &lt;Spacer spacing="large" /&gt;
            BOTTOM
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;HUGE&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            padding_x="small"
            padding_y="tiny"
        &gt;
            TOP
            &lt;Spacer spacing="huge" /&gt;
            BOTTOM
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;script&gt;
    import {
        Box,
        Spacer,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;BLOCK / DEFAULT&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            margin_top="small"
            padding_x="small"
            padding_y="tiny"
        &gt;
            TOP
            &lt;Spacer spacing="huge" /&gt;
            BOTTOM
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;INLINE / DEFAULT&lt;/Text&gt;
        &lt;Box
            palette="auto-inverse"
            margin_top="small"
            padding_x="small"
            padding_y="tiny"
        &gt;
            LEFT
            &lt;Spacer
                variation="inline"
                orientation="horizontal"
                spacing="huge"
            /&gt;
            RIGHT
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;
