&lt;script&gt;
    import {Box, Stack} from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack class="stack-preview"&gt;
    &lt;Box palette="alert" /&gt;
    &lt;Box palette="affirmative" /&gt;
    &lt;Box palette="negative" /&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.stack-preview .box) {
        width: 3rem;
        height: 3rem;
    }
&lt;/style&gt;

&lt;script&gt;
    import {
        Box,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="stack-orientation"
    alignment_y="top"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Box
            palette="dark"
            padding="small"
            width="content-min"
        &gt;
            &lt;Stack&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;HORIZONTAL&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Stack orientation="horizontal"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.stack-orientation .stack .box) {
        width: 3rem;
        height: 3rem;
    }
&lt;/style&gt;

