&lt;script&gt;
    import {
        Box,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="stack-alignment"
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;div&gt;
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt;
        &lt;Box
            palette="dark"
            padding="small"
            width="content-min"
        &gt;
            &lt;Stack&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;CENTER X/Y&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Stack alignment="center"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;STRETCH X&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Stack alignment_x="stretch"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;LEFT X&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Stack alignment_x="left"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;RIGHT X&lt;/Text&gt;
        &lt;Box palette="dark" padding="small"&gt;
            &lt;Stack alignment_x="right"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;TOP Y&lt;/Text&gt;
        &lt;Box
            palette="dark"
            padding="small"
            width="content-min"
        &gt;
            &lt;Stack alignment_y="top"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;

    &lt;div&gt;
        &lt;Text is="strong"&gt;BOTTOM Y&lt;/Text&gt;
        &lt;Box
            palette="dark"
            padding="small"
            width="content-min"
        &gt;
            &lt;Stack alignment_y="bottom"&gt;
                &lt;Box palette="alert" /&gt;
                &lt;Box palette="affirmative" /&gt;
                &lt;Box palette="negative" /&gt;
            &lt;/Stack&gt;
        &lt;/Box&gt;
    &lt;/div&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.stack-alignment .stack .box) {
        min-width: 3rem;
        min-height: 3rem;
    }

    :global(.stack-alignment
            .stack[data-alignment~="center"]) {
        width: 6rem;
        height: 12rem;
    }

    :global(.stack-alignment
            .stack[data-alignment-x~="stretch"]) {
        width: 6rem;
        height: 12rem;
    }

    :global(.stack-alignment
            .stack[data-alignment-x~="left"]),
    :global(.stack-alignment
            .stack[data-alignment-x~="right"]) {
        width: 6rem;
    }

    :global(.stack-alignment
            .stack[data-alignment-y~="top"]),
    :global(.stack-alignment
            .stack[data-alignment-y~="bottom"]) {
        height: 12rem;
    }
&lt;/style&gt;

&lt;script&gt;
    import {Box, Stack} from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    class="stack-wrap"
    orientation="horizontal"
    spacing="huge"
    variation="wrap"
&gt;
    &lt;Box palette="alert" /&gt;
    &lt;Box palette="affirmative" /&gt;
    &lt;Box palette="negative" /&gt;

    &lt;Box palette="alert" /&gt;
    &lt;Box palette="affirmative" /&gt;
    &lt;Box palette="negative" /&gt;

    &lt;Box palette="alert" /&gt;
&lt;/Stack&gt;

&lt;style&gt;
    :global(.stack-wrap .box) {
        width: 6rem;
        height: 6rem;
    }
&lt;/style&gt;
