&lt;script&gt;
    import {
        Box,
        Button,
        ContextButton,
        Offscreen,
        Stack,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Button for="offscreen-preview" palette="accent"&gt;
    Open Offscreen
&lt;/Button&gt;

&lt;Offscreen
    logic_id="offscreen-preview"
    placement="top"
    hidden
    captive
    dismissible
&gt;
    &lt;Box palette="accent" padding="medium"&gt;
        &lt;Stack
            orientation="horizontal"
            alignment_y="center"
            spacing="medium"
        &gt;
            &lt;ContextButton
                palette="light"
                variation="clear"
            &gt;
                X
            &lt;/ContextButton&gt;

            Well, hello there!
        &lt;/Stack&gt;
    &lt;/Box&gt;
&lt;/Offscreen&gt;

