&lt;Button
    for="offscreen-hidden"
    palette="accent"
    hidden={["tablet", "desktop", "widescreen"]}
&gt;
    Open HIDDEN Offscreen
&lt;/Button&gt;

&lt;Offscreen
    logic_id="offscreen-hidden"
    placement="top"
    hidden="mobile"
    captive
    dismissible
&gt;
    &lt;Box palette="accent" padding="medium"&gt;
        &lt;Stack
            orientation="horizontal"
            alignment_y="center"
            spacing="medium"
        &gt;
            &lt;ContextButton
                palette="light"
                variation="clear"
                hidden={[
                    "tablet",
                    "desktop",
                    "widescreen",
                ]}
            &gt;
                X
            &lt;/ContextButton&gt;

            I was HIDDEN on MOBILE!
        &lt;/Stack&gt;
    &lt;/Box&gt;
&lt;/Offscreen&gt;

