&lt;/script&gt;

&lt;Button for="overlay-toggleable"&gt;Toggle Modal&lt;/Button&gt;

&lt;Overlay logic_id="overlay-toggleable"&gt;
    &lt;Card.Container
        palette="auto"
        max_width="viewport-75"
    &gt;
        &lt;Card.Header&gt;Toggleable Modal&lt;/Card.Header&gt;

        &lt;Card.Footer&gt;
            &lt;ContextButton&gt;Dismiss&lt;/ContextButton&gt;
        &lt;/Card.Footer&gt;
    &lt;/Card.Container&gt;
&lt;/Overlay&gt;

&lt;script&gt;
