    import {
        Button,
        Card,
        ContextButton,
        Overlay,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Button for="overlay-non-dismissible"&gt;
    Open NON DISMISSIBLE Modal
&lt;/Button&gt;

&lt;Button for="overlay-is-dismissible" palette="accent"&gt;
    Open DISMISSIBLE Modal
&lt;/Button&gt;

&lt;Overlay logic_id="overlay-non-dismissible" captive&gt;
    &lt;Card.Container
        palette="auto"
        max_width="viewport-75"
    &gt;
        &lt;Card.Header&gt;
            NON DISMISSIBLE Modal
        &lt;/Card.Header&gt;

        &lt;Card.Footer&gt;
            &lt;ContextButton&gt;Dismiss&lt;/ContextButton&gt;
        &lt;/Card.Footer&gt;
    &lt;/Card.Container&gt;
&lt;/Overlay&gt;

&lt;Overlay
    logic_id="overlay-is-dismissible"
    captive
    dismissible
&gt;
    &lt;Card.Container
        palette="auto"
        max_width="viewport-75"
    &gt;
        &lt;Card.Header&gt;DISMISSIBLE Modal&lt;/Card.Header&gt;

        &lt;Card.Footer&gt;
            &lt;ContextButton&gt;Dismiss&lt;/ContextButton&gt;
        &lt;/Card.Footer&gt;
    &lt;/Card.Container&gt;
&lt;/Overlay&gt;

&lt;script&gt;
    import {
        Button,
        Card,
        ContextButton,
        Overlay,
    } from "@kahi-ui/framework";

    let toggle = false;

    const on_click = () =&gt; (toggle = !toggle);
&lt;/script&gt;

&lt;Button on:click={on_click}&gt;Toggle Modal&lt;/Button&gt;

&lt;Overlay logic_id="overlay-state" bind:state={toggle}&gt;
    &lt;Card.Container
        palette="auto"
        max_width="viewport-75"
    &gt;
        &lt;Card.Header&gt;Toggleable Modal&lt;/Card.Header&gt;

        &lt;Card.Footer&gt;
            &lt;ContextButton&gt;Dismiss&lt;/ContextButton&gt;
        &lt;/Card.Footer&gt;
    &lt;/Card.Container&gt;
&lt;/Overlay&gt;

&lt;script&gt;
    import {
        Button,
        Code,
        Overlay,
        Tile,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Overlay
    alignment_x="right"
    alignment_y="bottom"
    padding_bottom="medium"
    padding_right="medium"
&gt;
    &lt;Tile.Container
        palette="auto"
        elevation="medium"
        width="content-max"
        max_width="viewport-75"
    &gt;
        &lt;Tile.Section&gt;
            &lt;Tile.Header&gt;File Deleted&lt;/Tile.Header&gt;

            &lt;Text&gt;
                &lt;Code&gt;important_file.docx&lt;/Code&gt; was deleted
                from cloud storage.
            &lt;/Text&gt;
        &lt;/Tile.Section&gt;

        &lt;Tile.Footer&gt;
            &lt;Button
                palette="negative"
                data-size="small"
            &gt;
                X
            &lt;/Button&gt;
        &lt;/Tile.Footer&gt;
    &lt;/Tile.Container&gt;
&lt;/Overlay&gt;

