&lt;script&gt;
    import {
        Card,
        ContextButton,
        Menu,
        Popover,
        Spacer,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Popover
    logic_id="popover-preview"
    alignment_x="right"
    spacing="medium"
    dismissible
    hidden
&gt;
    &lt;ContextButton palette="accent"&gt;
        Open Popover
    &lt;/ContextButton&gt;

    &lt;Card.Container
        palette="auto"
        elevation="medium"
        max_width="content-max"
    &gt;
        &lt;Card.Section&gt;
            &lt;Menu.Container&gt;
                &lt;Menu.Button&gt;
                    Copy
                    &lt;Spacer
                        variation="inline"
                        spacing="medium"
                    /&gt;

                    &lt;Text is="kbd"&gt;CTRL+C&lt;/Text&gt;
                &lt;/Menu.Button&gt;

                &lt;Menu.Button&gt;
                    Cut
                    &lt;Spacer
                        variation="inline"
                        spacing="medium"
                    /&gt;

                    &lt;Text is="kbd"&gt;CTRL+X&lt;/Text&gt;
                &lt;/Menu.Button&gt;

                &lt;Menu.Divider /&gt;

                &lt;Menu.Button&gt;
                    Delete
                    &lt;Spacer
                        variation="inline"
                        spacing="medium"
                    /&gt;

                    &lt;Text is="kbd"&gt;DEL&lt;/Text&gt;
                &lt;/Menu.Button&gt;
            &lt;/Menu.Container&gt;
        &lt;/Card.Section&gt;
    &lt;/Card.Container&gt;
&lt;/Popover&gt;

&lt;script&gt;
    import {
        Box,
        ContextButton,
        Popover,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Popover
    logic_id="popover-hidden"
    alignment_x="right"
    hidden="mobile"
    spacing="medium"
    dismissible
&gt;
    &lt;ContextButton
        palette="accent"
        hidden={["tablet", "desktop", "widescreen"]}
    &gt;
        Open HIDDEN Popover
    &lt;/ContextButton&gt;

    &lt;Box
        palette="auto-inverse"
        max_width="content-max"
        padding="medium"
    &gt;
        This was HIDDEN on MOBILE only.
    &lt;/Box&gt;
&lt;/Popover&gt;

