snippets.script:
&lt;script&gt;
    import {
        Box,
        Stack,
        Text,
    } from "@kahi-ui/framework";
&lt;/script&gt;

&lt;Stack
    orientation="horizontal"
    spacing="medium"
    variation="wrap"
&gt;
    &lt;Box&gt;
        This is a
        &lt;Text is="strong"&gt;DEFAULT&lt;/Text&gt; Box!
    &lt;/Box&gt;

    &lt;Box palette="accent"&gt;
        This is a
        &lt;Text is="strong"&gt;ACCENT&lt;/Text&gt; Box!
    &lt;/Box&gt;

    &lt;Box palette="dark"&gt;
        This is a
        &lt;Text is="strong"&gt;DARK&lt;/Text&gt; Box!
    &lt;/Box&gt;

    &lt;Box palette="light"&gt;
        This is a
        &lt;Text is="strong"&gt;LIGHT&lt;/Text&gt; Box!
    &lt;/Box&gt;

    &lt;Box palette="alert"&gt;
        This is a
        &lt;Text is="strong"&gt;ALERT&lt;/Text&gt; Box!
    &lt;/Box&gt;

    &lt;Box palette="affirmative"&gt;
        This is a
        &lt;Text is="strong"&gt;AFFIRMATIVE&lt;/Text&gt; Box!
    &lt;/Box&gt;

    &lt;Box palette="negative"&gt;
        This is a
        &lt;Text is="strong"&gt;NEGATIVE&lt;/Text&gt; Box!
    &lt;/Box&gt;
&lt;/Stack&gt;
